import React, { useState } from 'react';
import Navbar from '../components/common/Navbar';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('Member');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Username:', username);
    console.log('Password:', password);
    console.log('Role:', role);
    // Add your login logic here
  };

  return (
    <div> <Navbar/>
      <div className='text-white flex flex-col justify-center items-center h-screen '> 
      <div className="h-1/2 w-1/3 border flex flex-col justify-center items-center">
      <h1 className='text-3xl font-bold	'>Login</h1>
      <form onSubmit={handleSubmit}>
        <div className='mt-7'>
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            style={{ color: 'black', marginLeft: '20px' }} 
           />
        </div>
        <div className='mt-2'>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{ color: 'black' , marginLeft: '22px' }}
          />
        </div>
        <div className='mt-2'>
          <label htmlFor="role" classNam>Login as:</label>
          <select
            id="role"
            value={role}
            onChange={(e) => setRole(e.target.value)}
            style={{ color: 'black', marginLeft: '20px' }}
          >
            <option value="Admin">Admin</option>
            <option value="Member">Member</option>
          </select>
        </div>
        <button type="submit" className='mt-7 translate-x-[150%] bg-richblack-500 text-black px-4 py-1' >Login</button>
      </form>
        <div className="mt-4 text-center">
          <p>Not a member? <a href="/signup" className="text-blue-500">Signup</a></p>
        </div>
      </div>
    </div>
    </div>
  );
};

export default Login;
