import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/common/Navbar';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('member');
  const navigate = useNavigate();
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:8080/${role}/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      console.log(data);

      if (data.data !== null) {
        if (role === 'admin') {
          navigate('/admin', { state: { adminId: data.data.adminId } });
        } else if (role === 'member') {
          navigate('/member');
        }
      } else {
        setError(data.error.message);
      }
    } catch (error) {
      console.error('Error:', error);
      setError('An error occurred. Please try again.');
    }
  };

  return (
    <div>
      <Navbar />
      <div className='text-white flex flex-col justify-center items-center h-screen'>
        <div className="h-1/2 w-1/3 border flex flex-col justify-center items-center">
          <h1 className='text-3xl font-bold'>Login</h1>
          <form onSubmit={handleSubmit}>
            <div className='mt-7'>
              <label htmlFor="email">Email:</label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                style={{ color: 'black', marginLeft: '53px' }}
              />
            </div>
            <div className='mt-2'>
              <label htmlFor="password">Password:</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                style={{ color: 'black', marginLeft: '22px' }}
              />
            </div>
            <div className='mt-2'>
              <label htmlFor="role">Login as:</label>
              <select
                id="role"
                value={role}
                onChange={(e) => setRole(e.target.value)}
                style={{ color: 'black', marginLeft: '33px' }}
              >
                <option value="admin">Admin</option>
                <option value="member">Member</option>
              </select>
            </div>
            <button type="submit" className='mt-7 translate-x-[150%] bg-richblack-500 text-black px-4 py-1'>Login</button>
          </form>
          {error && <p className="text-red-500 mt-3">{error}</p>}
        </div>
        <div className="mt-4 text-center">
          <p>Not a member? <a href="/signup" className="text-blue-500">Signup</a></p>
        </div>
      </div>
    </div>
  );
};

export default Login;
